import { StyleSheet, Text, View, Image } from 'react-native'
import tw from 'tailwind-react-native-classnames'
import shareVideo from '../data/share.mp4';
import React, { useState } from 'react'
import { TextInput, Button } from 'react-native-paper';
import { useStateContext } from '../context/ContextProvider'
import Axios from 'axios';

const Register = ({navigation}) => {
    const [name, setName] = useState('');
    const [password, setPassword] = useState('');
    const [correct,setCorrect] = useState(true);
    const [gender, setGender] = useState('');
    const [destination, setDestination] = useState('');
    const { setLogin, setprofile } = useStateContext();

    const handleClick = () => {
        Axios.post("http://localhost:3001/register", {
      name: name,
      password: password,
      gender: gender,
      destination: destination
    }).then(() => {
        setLogin(true)
        setprofile({
            name: name,
            gender: gender,
            destination: destination
        })
    });
    navigation.navigate("HomeTabs")
    }
        return (
        <View style={tw`flex justify-start items-center flex-col `}>
            <View style={tw`relative w-full h-full`}>
                <video
                    src={shareVideo}
                    type="video/mp4"
                    loop
                    controls={false}
                    muted
                    autoPlay
                    style={styles.video}
                />
                <View style={tw`absolute flex flex-col justify-center items-center top-0 right-0 left-0 bottom-0`}>
                    <TextInput
                        style={tw`w-64`}
                        theme={{ roundness: 50 }}
                        mode='outlined'
                        label="username"
                        onChangeText={text => setName(text)}
                        left={<TextInput.Icon icon="account" />}
                    />
                    <View style={tw`pt-1`}></View>
                    <TextInput
                        style={tw`w-64`}
                        theme={{ roundness: 50 }}
                        mode='outlined'
                        label="password"
                        secureTextEntry={true}
                        onChangeText={text => setPassword(text)}
                        left={<TextInput.Icon icon="lock" />}
                    />
                    {correct && <View style={tw`pt-1`}></View>}
                    {!correct && <View style={tw`text-red-700 text-xs`}><Text>password is incorrect</Text></View>}
                    <TextInput
                        style={tw`w-64`}
                        theme={{ roundness: 50 }}
                        mode='outlined'
                        label="confirm password"
                        secureTextEntry={true}
                        onChangeText={text => {if(text==password){setCorrect(true)}else{setCorrect(false)}}}
                        left={<TextInput.Icon icon="lock" />}
                    />
                    {correct && <View style={tw`pt-1`}></View>}
                    {!correct && <View style={tw`text-red-700 text-xs`}><Text>password is incorrect</Text></View>}
                    <TextInput
                        style={tw`w-64`}
                        theme={{ roundness: 50 }}
                        mode='outlined'
                        label="gender"
                        onChangeText={text => setGender(text)}
                        left={<TextInput.Icon icon="gender-male-female" />}
                    />
                    <View style={tw`pt-1`}></View>
                    <TextInput
                        style={tw`w-64`}
                        theme={{ roundness: 50 }}
                        mode='outlined'
                        label="prefer destination"
                        onChangeText={text => setDestination(text)}
                        left={<TextInput.Icon icon="google-maps" />}
                    />
                    <View style={tw`pt-1`}></View>
                    <View style={tw`flex-row`}>
                        <Button
                            style={tw`w-28 p-1`}
                            theme={{ roundness: 50 }}
                            mode="contained"
                            contentStyle={{ flexDirection: 'row-reverse' }}
                            onPress={() => handleClick()}
                        >
                            Register
                        </Button>
                    </View>
                </View>
            </View>
        </View>
    )
}

export default Register

const styles = StyleSheet.create({})
