import { StyleSheet, Text, SafeAreaView, View,Image } from 'react-native'
import tw from 'tailwind-react-native-classnames'
import React,{useState} from 'react'

const ChatbotScreen = () => {
  const [chats,setChats] = useState([
    {
      name: 'bot',
      text:"babababa"
    },
    {
      name:'user',
      text:"bababa"
    },
    {
      name: 'bot',
      text:"babababa"
    },
    {
      name:'user',
      text:"bababa"
    }
  ])
        {/*(chat.name === 'bot' && <Text style={tw`bg-blue-400`}>{`${chat.name} : ${chat.text}`}</Text>
        )*/}
  return (
    <SafeAreaView style={tw`top-8`}>
      {/*<Text>ChatbotScreen</Text>*/}
      {chats.map((chat,index) =>{
        return(
          <Text style={tw`flex p-2`} key={`${chat.text}-${index}`}>
            {chat.name === 'bot' && 
            <><Image source={require('../assets/1.jpg')}/><Text style={tw`bg-blue-400 p-2 rounded-lg text-xl italic`}>
            {`${chat.name} : ${chat.text}`}
            </Text></>}
            {chat.name === 'user' && 
            <Text style={tw`bg-yellow-400 p-2 rounded-lg ml-auto text-xl italic`}>
            {`${chat.name} : ${chat.text}`}
            </Text>}
          </Text>
        )
      }

      )}
      {console.log(chats[0].name)}
    </SafeAreaView>
  )
}



export default ChatbotScreen

const styles = StyleSheet.create({
  chatbot: {
    backgroundColor: 'blue'
  },
  userbot: {

  }
})
