import { StyleSheet, Text, SafeAreaView, View, Image } from 'react-native'
import { TextInput, Button } from 'react-native-paper';
import tw from 'tailwind-react-native-classnames'
import React, { useState } from 'react'

const ChatbotScreen = () => {
  const [context, setContext] = useState('')
  const [tag1, setTag1] = useState(false)
  const [tag2, setTag2] = useState(true)
  const [chats, setChats] = useState([
    {
      name: 'bot',
      text: "babababa"
    },
    {
      name: 'user',
      text: "bababa"
    },
    {
      name: 'bot',
      text: "babababa"
    },
    {
      name: 'user',
      text: "bababa"
    },
    {
      name: 'bot',
      text: "babababa"
    },
    {
      name: 'user',
      text: "bababa"
    },
    {
      name: 'bot',
      text: "babababa"
    },
    {
      name: 'user',
      text: "bababa"
    },
    {
      name: 'bot',
      text: "babababa"
    },
    {
      name: 'user',
      text: "bababa"
    },
    {
      name: 'bot',
      text: "babababa"
    },
    {
      name: 'user',
      text: "bababa"
    }
  ])
  return (
    <>
    <SafeAreaView style={tw`top-8`}>
      {chats.map((chat, index) => {
        return (
          <Text style={tw`flex p-2`} key={`${chat.text}-${index}`}>
            {chat.name === 'bot' &&
              <><Image style={tw`w-8 h-8 p-1`} source={require('../assets/bot.png')} /><Text style={tw`bg-blue-400 p-2 rounded-lg text-xl italic`}>
                {chat.text}
              </Text></>}
            {chat.name === 'user' &&
              <><Text style={tw`bg-yellow-400 p-2 rounded-lg ml-auto text-xl italic`}>
                {chat.text}
              </Text>
                <Image style={tw`w-8 h-8 p-1`} source={require('../assets/avatar.png')} /></>}
          </Text>
        )
      }
      )}
    </SafeAreaView>

        <View style={tw`absolute bottom-0`}>
      <View style={tw`flex-row`}>
        <TextInput
          style={tw`w-64`}
          theme={{ roundness: 50 }}
          mode='outlined'
          onChangeText={text => setContext(text)}
          //onFocus={() => navigation.navigate("SetPickUp")}
        />
        <View style={tw`pt-1 pl-1`}>
          <Button
            style={tw`w-32 p-2`}
            theme={{ roundness: 50 }}
            mode="contained"
            //onPress={() => handleClick("schedule")}
          >
            Send
          </Button>
        </View>
      </View>
      </View>
      </>
  )
}



export default ChatbotScreen
