import { Text, View } from 'react-native'
import React, {useState} from 'react'
import { List } from 'react-native-paper';
import tw from 'tailwind-react-native-classnames'
import { useStateContext } from '../context/ContextProvider';
//import { }

const Schedule = () => {
  const [expanded, setExpanded] = React.useState(true);
  const { handleClick, isClicked} = useStateContext();
  const { scheduleTime,setScheduleTime } = useStateContext()
  const handlePress = () => setExpanded(!expanded);
  return (
    <View style={tw`w-11/12 h-55 bg-gray-300 bottom-0 rounded-lg absolute right-5`}>
          <List.Section>
    <List.Subheader style={tw`items-center text-center font-bold text-lg`}>Schedule a Ride</List.Subheader>
    <List.Item title={scheduleTime.toUTCString().slice(0,11)} style={tw`items-center`}/>
    <List.Item title="Second Item" style={tw`items-center`}/>
  </List.Section>
    </View>
  )
}

export default Schedule
