// In App.js in a new project

import { StatusBar } from 'expo-status-bar';
import React, {useRef, useState} from 'react';
import { StyleSheet,Dimensions, View, Text, FlatList, TouchableOpacity, Image } from 'react-native';
import carouselItem from '../data/dummy'

const {width,height} = Dimensions.get('window');
const viewConfigRef = {viewAreaCoveragePercentThreshold:95}
const [currentIndex,setCurrentIndex] = useState(0)
const HomeScreen = () => {
    let flatListRef = useRef()
    //const onViewRef = useRef((changed) => {
    //    if(changed[0].isViewable) {
    //        setCurrentIndex(changed[0].index)
    //    }
    //})

    const renderItems = ({item}) => {
        return (
        <TouchableOpacity 
        onPress={() => console.log("clicked")}
        activeOpacity={1}
        >
        <Image source={{uri:item.url}} style={styles.image} />
        <View style={styles.footer}>
            <Text style={styles.footerText}>{item.title}</Text>
            <Text style={styles.footerText}>{item.promo}</Text>
        </View>
        </TouchableOpacity>
        )
    }
    return (
        <View style={styles.container}>
            <StatusBar style='auto' />

            <FlatList 
            data={carouselItem} 
            renderItem={renderItems} 
            keyExtractor={(item, index) => index.toString()}
            horizontal
            showsHorizontalScrollIndicator={false}
            pagingEnabled
            ref={(ref) => {
                flatListRef.current=ref
            }}
            style={styles.carousel}
            viewabilityConfig={viewConfigRef}
            //onViewableItemsChanged={onViewRef.current}
            />
            <View style={styles.dotView}>
                {carouselItem.map(({},index) => (
                    <TouchableOpacity 
                    key={index.toString()}
                    style={styles.circle} />
                ))}
            </View>
        </View>

    );
}

const styles = StyleSheet.create(
    {
        container: {
            flex:1,
            backgroundColor:"#fff",
            //alignItems:'center',
            //justifyContent:"center",
        },
        carousel:{
            maxHeight: 300,

        },
        image: {
            width,
            height:250,
            resizeMode:"cover",
        },
        footer:{
            flexDirection:'row',
            justifyContent:'space-between',
            height:50,
            paddingHorizontal:40,
            alignItems:'center',
            backgroundColor:'#000',
        },
        footerText:{
            color:"#fff",
            fontSize:18,
            fontWeight:'bold'
        },
        dotView: {
            flexDirection:"row",
            justifyContent:"center",
            marginVertical:20,
        },
        circle:{
            width:10,
            height:10,
            backgroundColor:"grey",
            borderRadius:50,
            marginHorizontal:5,
        }
    }
)

export default HomeScreen;
