import { View, Image, Text } from 'react-native'
import { Button } from 'react-native-paper';
import tw from 'tailwind-react-native-classnames'
import React from 'react'
import { userProfile } from '../data/userProfile'
import Ionicons from 'react-native-vector-icons/Ionicons';

const MainAccountScreen = ({ navigation }) => {
    return (
        //<View >
        //  <View style={tw`flex-row h-20`}>
        //    <Text style={tw`text-3xl font-bold`}>Wang</Text>
        //    <Image 
        //    style={tw`h-full w-full`}
        //    source={{ uri: require('../assets/avatar.png') }} 
        //    />
        //  </View>
        //</View>

        <View style={tw`flex p-4 justify-between`}>
            <View style={tw`flex-row justify-between`}>
                <View style={tw`item-center text-xl pt-6 pl-2 font-bold`}>
                    {`${userProfile[0].firstname}  ${userProfile[0].lastname}`}
                </View>
                <Image
                    style={tw`rounded-full w-20 h-20`}
                    source={{ uri: require('../assets/avatar.png') }}
                >
                </Image>

            </View>
            <View style={tw`flex-row grid-cols-3 pt-10`}>
                <View style={tw`place-items-center text-xl`}>
                <Button style={tw`w-32 flex-row`} labelStyle={{fontSize: 35}} icon="help-circle"/>
                <Text style={tw`pl-5`}>Help</Text>
                </View>
                <View style={tw`place-items-center text-xl`}>
                <Button style={tw`w-32 flex-row`} labelStyle={{fontSize: 35}} icon="wallet-outline"/>
                <Text style={tw`text-center`}>Wallet</Text>
                </View>
                <View style={tw`place-items-center text-xl`}>
                <Button style={tw`w-32 flex-row`} labelStyle={{fontSize: 35}} icon="clock"/>
                <Text style={tw`text-center`}>Trips</Text>
                </View>
            </View>
            {/*<View style={tw`flew-row`}>
                <Button style={tw`w-32 flex-row`} icon="help-circle" size={20}/>
                <Button style={tw`w-32 flex-row`} icon="wallet-outline" />
                <Button style={tw`w-32 flex-row`} icon="clock" />
            </View>*/}
        </View>
    )
}

export default MainAccountScreen;
