import { StyleSheet, View, SafeAreaView, Image } from 'react-native'
import { TextInput, Button } from 'react-native-paper';
import tw from 'tailwind-react-native-classnames'
import { useStateContext } from '../context/ContextProvider'
import React, { useState, useEffect } from 'react'
import { postData } from '../fetchMethod'
import { backendhost } from '../configure' 

const SetPickUp = ({ navigation }) => {
  const { profile, scheduleTime } = useStateContext();
  const [destination, setDestination] = useState();
  const [showImage, setShowImage ] = useState(false)
  const [imagesrc, setImagesrc] = useState('')
  const handleClick = () => {
    console.log(destination)
    postData(backendhost+'map', { destination: destination})
    .then((image) => {
        console.log(image)
        setImagesrc(image.image_url)
        console.log(typeof(imagesrc))
        setShowImage(true)
    })
  }
  //useEffect(() => {setPlace(profile.destination)},[])
  console.log(scheduleTime)
  return (
    <SafeAreaView style={tw`top-8`}>
      <TextInput
        style={tw`w-full`}
        theme={{ roundness: 50 }}
        mode='outlined'
        label="Start Place"
        placeholder='Where to?'
        value="University Town, NUS"
        editable={false}
        left={<TextInput.Icon icon="magnify" />}
      //onFocus={() => navigation.navigate("SetPickUp")}
      />
      <View style={tw`pt-1`}></View>
      <TextInput
        style={tw`w-full`}
        theme={{ roundness: 50 }}
        mode='outlined'
        label="Go To Pin"
        placeholder={profile.destination}
        onChangeText={text => setDestination(text)}
        left={<TextInput.Icon icon="magnify" />}
      //onFocus={() => navigation.navigate("SetPickUp")}
      />
      <Button
        style={tw`w-32 p-2`}
        theme={{ roundness: 50 }}
        mode="contained"
        onPress={() => handleClick()}
      >
        Confirm
      </Button>
      {showImage && <Image style={tw`w-64 h-64 p-1`} source={require(`${imagesrc}`)}></Image>}
    </SafeAreaView>
  )
}

export default SetPickUp

const styles = StyleSheet.create({})
